import (
	"fmt"
	"log"
	"syscall"
	"unsafe"
)
// defining an imitation RAWINPUTDEVICELIST
type rawInputDeviceList struct {
	DeviceHandle uintptr
	Type         uint32
}
const RIDI_DEVICENAME uint32 = 0x20000007
var (
	user32                     = syscall.NewLazyDLL("user32.dll")
	getRawInputDeviceListProc  = user32.NewProc("GetRawInputDeviceList")
	getRawInputDeviceInfoWProc = user32.NewProc("GetRawInputDeviceInfoW")
)
func main() {
	log.SetFlags(0)
	size := uint32(unsafe.Sizeof(rawInputDeviceList{}))
	var devCount uint32
	if err := getRawInputDeviceList(nil, &devCount, size); err != nil {
		log.Fatal(err)
	}
	if devCount == 0 {
		log.Fatal("no devices")
	}
	devices := make([]rawInputDeviceList, devCount)
	if err := getRawInputDeviceList(&devices[0], &devCount, size); err != nil {
		log.Fatal(err)
	}
	const RIDI_DEVICENAME uint32 = 0x20000007
	for di := range devices {
		var devName [256]uint16
		size := uint32(unsafe.Sizeof(devName))
		sizeOK, err := getRawInputDeviceInfo(
			devices[di].DeviceHandle,
			RIDI_DEVICENAME,
			unsafe.Pointer(&devName[0]),
			&size,
		)
		if err != nil {
			log.Fatal(err)
		}
		if !sizeOK {
			log.Fatalf("insufficient size; want %d\n", size)
		}
		fmt.Println(syscall.UTF16ToString(devName[:size]))
	}
}
func getRawInputDeviceList(
	rawInputDeviceList *rawInputDeviceList,
	numDevices *uint32,
	size uint32,
) error {
	r1, _, err := getRawInputDeviceListProc.Call(
		uintptr(unsafe.Pointer(rawInputDeviceList)),
		uintptr(unsafe.Pointer(numDevices)),
		uintptr(size))
	if r1 == uintptr(^uint32(0)) {
		return err
	}
	return nil
}
func getRawInputDeviceInfo(
	deviceHandle uintptr,
	uiCommand uint32,
	pData unsafe.Pointer,
	size *uint32,
) (bool, error) {
	r1, _, err := getRawInputDeviceInfoWProc.Call(
		uintptr(deviceHandle),
		uintptr(uiCommand),
		uintptr(pData),
		uintptr(unsafe.Pointer(size)))
	if err != syscall.Errno(0) {
		return true, err
	}
	return r1 > 0, nil
}

// FindValue enables support for values residing either directly in the given context or in the wrapped fiber UserValues hidden by the fiber Adaptor conversion.
func FindValue(ctx context.Context, key any) (value any) {
	if value = ctx.Value(key); value == nil {
		value = getUserCtxFromFiberRequestCtx(ctx).Value(key)
	}
	return value
}
// getUserCtxFromFiberRequestCtx allows to access util-server specific context values for contexts which come from the fiber Adaptor.
// This method returns either the hidden user context in case it's a proper Adaptor, context or otherwise just the incoming adaptor.
func getUserCtxFromFiberRequestCtx(ctx context.Context) (extracted context.Context) {
	if localUserCtx := ctx.Value("__local_user_context__"); localUserCtx != nil {
		if localUsrCtxCast, ok := localUserCtx.(context.Context); ok {
			return localUsrCtxCast
		}
	}
	return ctx
}

