    addr := uintptr(ptr)
    fmt.Printf("Func Addr: %v\n", addr)
    maindll := syscall.NewLazyDLL("CSharp_Go.dll")
    //maindll := syscall.NewLazyDLL("Cpp_Go.dll")
    setAddFunc := maindll.NewProc("SetAddFunc")
    add := maindll.NewProc("Add")
    r1, r2, err := setAddFunc.Call(addr)
    fmt.Println(r1, r2, err)
    r1, r2, err = add.Call(a, b)
    fmt.Println(r1, r2, err)
    fmt.Scanln(&input)
    fmt.Println(input)
}

package action
import (
	"bufio"
	"context"
	"log"
	"os"
	"sync"
	lua "github.com/yuin/gopher-lua"
	"github.com/yuin/gopher-lua/parse"
)
var Actions chan *Action
type Action struct {
	Repeating  bool
	ScriptPath string
	ClientIP   string
	ClientPort string
	ClientNet  string
	Username   string
	Password   string
	Protocol   string
}
func (a *Action) ToLuaTable() *lua.LTable {
	tab := &lua.LTable{}
	tab.RawSet(lua.LString("Repeating"), lua.LBool(a.Repeating))
	tab.RawSet(lua.LString("ClientIP"), lua.LString(a.ClientIP))
	tab.RawSet(lua.LString("ClientPort"), lua.LString(a.ClientPort))
	tab.RawSet(lua.LString("ClientNet"), lua.LString(a.ClientNet))
	tab.RawSet(lua.LString("Username"), lua.LString(a.Username))
	tab.RawSet(lua.LString("Password"), lua.LString(a.Password))
	tab.RawSet(lua.LString("Protocol"), lua.LString(a.Protocol))
	return tab
}
// I borrow it from https://github.com/yuin/gopher-lua?tab=readme-ov-file#sharing-lua-byte-code-between-lstates
// ============================
// ============================
// CompileLua reads the passed lua file from disk and compiles it.
func CompileLua(filePath string) (*lua.FunctionProto, error) {
	file, err := os.Open(filePath)
	defer file.Close()
	if err != nil {
		return nil, err
	}
	reader := bufio.NewReader(file)
	chunk, err := parse.Parse(reader, filePath)
	if err != nil {
		return nil, err
	}
	proto, err := lua.Compile(chunk, filePath)
	if err != nil {
		return nil, err
	}
	return proto, nil
}
// ============================
// ============================
func DoCompiledProto(L *lua.LState, proto *lua.FunctionProto, a *Action) error {
	lFunc := L.NewFunctionFromProto(proto)
	L.Push(lFunc)
	tab := a.ToLuaTable()
	L.Push(tab)
	return L.PCall(1, lua.MultRet, nil)
}
var luaProtos sync.Map
func Worker(ctx context.Context) {
	var a *Action
	Actions = make(chan *Action)
	for {
		select {
		case <-ctx.Done():
			break
		case a = <-Actions:
			func() {
				L := lua.NewState()
				defer L.Close()
				// Run FunctionProto if it cached
				if proto, ok := luaProtos.Load(a.ScriptPath); ok {
					if err := DoCompiledProto(L, proto.(*lua.FunctionProto), a); err != nil {
						log.Print(err)
						return
					}
				} else {
					// Compile and cache FunctionProto if cache miss
					if newProto, err := CompileLua(a.ScriptPath); err != nil {
						log.Print(err)
						return
					} else {
						luaProtos.Store(a.ScriptPath, newProto)
						// Run FunctionProto
						if err := DoCompiledProto(L, newProto, a); err != nil {
							log.Print(err)
							return
						}
					}
				}
			}()
		}
	}
}

public enum OrderStatus { Pending, Declined, Confirmed };
public class Order
{
  public OrderStatus Status { get; init; }
  public string Value { get; init; }
  public void Decline() => SetStatus(OrderStatus.Declined);
  public void Confirm() => SetStatus(OrderStatus.Confirmed);
  private void SetStatus(OrderStatus value)
  {
    // Business rule is valid for all use cases of the context
    if (Status != OrderStatus.Pending)
    {
      throw new InvalidOperationException();
    }
    Status = value;
  }
}

