func counter(startFrom int, multiplyBy int, quit, pause <-chan bool, last *int) {
	for {
		for x := startFrom; x < 100; x++ {
			time.Sleep(time.Millisecond * 1000)
			select {
			case <-quit:
				fmt.Printf("counter stopped")
				return
			case msg := <-pause:
				if msg == true {
					fmt.Printf("counter paused")
				pause_loop:
					for {
						select {
						case msg := <-pause:
							if msg == false {
								fmt.Printf("counter resumed")
								break pause_loop
							}
						case <-quit:
							fmt.Printf("counter stopped")
							return
						}
					}
				}
			default:
				result := x * multiplyBy
				*last = result
				fmt.Printf("%d", result)
			}
		}
	}
}

func counter(
	startFrom, multiplyBy int64,
	paused, quit <-chan bool,
	last *int64,
) {
	var pause bool
	// if startFrom higher 100
	// loop exit and function end
	for startFrom < 100 {
		select {
		// get signal pause
		case p, ok := <-paused:
			// check signal is ok?
			if ok {
				pause = p
			}
		case <-quit:
			fmt.Printf("counter stopped")
			return
		// sleep 1s if not get data
		// from any channels
		// if get new data from channels
		// time restarted
		// and started again
		case <-time.After(time.Millisecond * 1000):
			// if not pause work
			if !pause {
				result := startFrom * multiplyBy
				// use swap atomic
				// if many goroutine runs
				// don't get problem!
				atomic.SwapInt64(last, result)
				startFrom++
				fmt.Printf("%d", result)
			}
		}
	}
}

