import (
	"encoding/json"
	"fmt"
	"github.com/aws/aws-lambda-go/lambda"
)
type event struct {
	Type string          `json:"type"`
	Data json.RawMessage `json:"data"`
}
type CognitoEventUserPoolsPreSignup struct{}
type CognitoEventUserPoolsCustomMessage struct{}
func getData[T CognitoEventUserPoolsPreSignup | CognitoEventUserPoolsCustomMessage](data json.RawMessage) (T, error) {
	var d T
	err := json.Unmarshal(data, &d)
	return d, err
}
func handler(e event) (any, error) {
	switch e.Type {
	case "pre-signup":
		data, err := getData[CognitoEventUserPoolsPreSignup](e.Data)
		if err != nil {
			return nil, err
		}
		return presignup(data)
	case "custom-message":
		data, err := getData[CognitoEventUserPoolsCustomMessage](e.Data)
		if err != nil {
			return nil, err
		}
		return customMessage(data)
	default:
		return nil, fmt.Errorf("unknown event type: %s", e.Type)
	}
}
func presignup(data CognitoEventUserPoolsPreSignup) (CognitoEventUserPoolsPreSignup, error) {
	return data, nil
}
func customMessage(data CognitoEventUserPoolsCustomMessage) (CognitoEventUserPoolsCustomMessage, error) {
	return data, nil
}
func main() {
	lambda.Start(handler)
}

