    for {
        select {
        case event, ok := <-rmq.secretWatcher.Events:
            if !ok {
                return
            }
            if event.Has(fsnotify.Remove) || event.Has(fsnotify.Write) {
                ilog.Info(event.Name)
            }
        case err, ok := <-rmq.secretWatcher.Errors:
            if !ok {
                return
            }
            ilog.Errorf("Error in watcher: %v", err)
        case <-rmq.done:
            return
        }
    }
}

