func insert(db *sqlx.DB) error {
	// Start the transaction.
	tx, err := db.Beginx()
	if err != nil {
		return err
	}
	var aId int
	// The returned id is stored in the variable "aId".
	if err := tx.Get(&aId, "INSERT INTO a DEFAULT VALUES RETURNING id"); err != nil {
		tx.Rollback()
		return err
	}
	var bId int
	// Use the insert id from table A.
	if err := tx.Get(&bId, "INSERT INTO b (a_id) VALUES ($1) RETURNING id", aId); err != nil {
		// In case of an error, rollback the transaction, this is the case where the A entry
		// is automatically removed as well.
		tx.Rollback()
		return err
	}
	// Else commit the transaction.
	if err := tx.Commit(); err != nil {
		return err
	}
	return nil
}

