var errInvalid = errors.New("invalid")
func parseTimestamp(s string) (time.Time, error) {
    if len(s) != 16 || s[2] != '-' || s[5] != '-' || s[10] != ' ' || s[13] != ':' {
        return time.Time{}, errInvalid
    }
    var errAtoi error
    atoi := func(num string) int {
        n, err := strconv.Atoi(num)
        if errAtoi == nil {
            errAtoi = err
        }
        return n
    }
    day := atoi(s[:2])
    month := time.Month(atoi(s[3:5]))
    year := atoi(s[6:10])
    hour := atoi(s[11:13])
    minute := atoi(s[14:16])
    if errAtoi != nil {
        return time.Time{}, errAtoi
    }
    return time.Date(year, month, day, hour, minute, 0, 0, time.UTC), nil
}

package main
import (
	"flag"
	"fmt"
	"log"
	"time"
	"github.com/nats-io/nats-server/v2/server"
	"github.com/nats-io/nats.go"
)
func main() {
	var (
		host           string
		port           int
		certFile       string
		keyFile        string
		clientCertFile string
		clientKeyFile  string
		caFile         string
	)
	flag.StringVar(&host, "host", "localhost", "Client connection host/IP.")
	flag.IntVar(&port, "port", 4222, "Client connection port.")
	flag.StringVar(&certFile, "tls.cert.server", "cert.pem", "TLS cert file.")
	flag.StringVar(&keyFile, "tls.key.server", "key.pem", "TLS key file.")
	flag.StringVar(&caFile, "tls.ca", "ca.pem", "TLS CA file.")
	flag.StringVar(&clientCertFile, "tls.cert.client", "client-cert.pem", " Client TLS cert file.")
	flag.StringVar(&clientKeyFile, "tls.key.client", "client-key.pem", "Client TLS key file.")
	flag.Parse()
	serverTlsConfig, err := server.GenTLSConfig(&server.TLSConfigOpts{
		CertFile: certFile,
		KeyFile:  keyFile,
		CaFile:   caFile,
		Verify:   true,
		Timeout:  2,
	})
	if err != nil {
		log.Fatalf("tls config: %v", err)
	}
	// Setup the embedded server options.
	opts := server.Options{
		Host:      host,
		Port:      port,
		TLSConfig: serverTlsConfig,
	}
	// Initialize a new server with the options.
	ns, err := server.NewServer(&opts)
	if err != nil {
		log.Fatalf("server init: %v", err)
	}
	// Start the server.
	go ns.Start()
	defer ns.Shutdown()
    // Allow the server to startup before creating the connection.
	time.Sleep(time.Second)
	// Create a client connection.
	nc, err := nats.Connect(
		fmt.Sprintf("tls://%s:%d", host, port),
		nats.RootCAs(caFile),
		nats.ClientCert(clientCertFile, clientKeyFile),
	)
	if err != nil {
		log.Fatalf("client connect: %v", err)
	}
	defer nc.Drain()
	// Setup a basic subscription, publish, and print it.
	sub, _ := nc.SubscribeSync("hello")
	defer sub.Drain()
	nc.Publish("hello", []byte("world"))
	msg, _ := sub.NextMsg(time.Second)
	fmt.Println(string(msg.Data))
}

import (
    "bytes"
    "encoding/base64"
    "io"
    "os"
)
func EncodePath(path string) ([]byte, error) {
    data, err := os.ReadFile(path)
    if err != nil {
        return nil, err
    }
    output := make([]byte, base64.StdEncoding.EncodedLen(len(data)))
    base64.StdEncoding.Encode(output, data)
    return output, nil
}
func EncodeReader(rd io.Reader) ([]byte, error) {
    var b bytes.Buffer
    enc := base64.NewEncoder(base64.StdEncoding, &b)
    if _, err := io.Copy(enc, rd); err != nil {
        return nil, err
    }
    if err := enc.Close(); err != nil {
        return nil, err
    }
    return b.Bytes(), nil
}

