import (
	"bufio"
	"fmt"
	"io"
	"os"
)
var m = map[string]bool{}
func Compare() {
	// open the first document
	file1, _ := os.Open("Links_New.txt")
	defer file1.Close()
	// open the second document
	file2, _ := os.Open("Links_Old.txt")
	defer file2.Close()
	// create scanners for both documents
	r1 := bufio.NewReader(file1)
	// initialize a counter for the number of New lines
	NewLines := 0
	// loop through each line in the first file
	for {
		line, _, c := r1.ReadLine()
		m[string(line)] = true
		if c == io.EOF {
			break
		}
	}
	r2 := bufio.NewReader(file2)
	for {
		line, _, c := r2.ReadLine()
		if !m[string(line)] {
			NewLines++
			fmt.Printf("%s\n", line)
		}
		if c == io.EOF {
			break
		}
	}
	// print the number of new lines
	fmt.Println("\n\n Number of new lines:", NewLines)
}
func main() {
	Compare()
}

func getCounts(respBody io.Reader) (counts []string, err error) {
	z := html.NewTokenizer(respBody)
	for {
		if z.Next() == html.ErrorToken {
			if err := z.Err(); err != io.EOF {
				return nil, err
			}
			break
		}
		t := z.Token()
		if t.Type == html.StartTagToken && t.Data == "div" {
			for _, a := range t.Attr {
				if a.Key == "class" && a.Val == "profile-data__count-number" {
					if z.Next() == html.TextToken {
						counts = append(counts, z.Token().Data)
						break
					}
				}
			}
		}
	}
	return counts, nil
}

