		dynaClient.DeleteTable(context.TODO(), &dynamodb.DeleteTableInput{
			TableName: aws.String("colors"),
		})
	}()
	// load table
	dynaClient.PutItem(context.TODO(), &dynamodb.PutItemInput{
		TableName: aws.String("colors"),
