	"io"
	"strings"
	"golang.org/x/net/html"
)
func main() {
	s := `
    <a href="http://example.com/1">Go to <b>example</b> 1</a>
    <p>Some para text</p>
    <a href="http://example.com/2">Go to <b>example</b> 2</a>
    `
	doc, _ := html.Parse(strings.NewReader(s))
	var nAnchor *html.Node
	var sTxt string
	var bufInnerHtml bytes.Buffer
	w := io.Writer(&bufInnerHtml)
	var f func(*html.Node)
	f = func(n *html.Node) {
		if n.Type == html.ElementNode && n.Data == "a" {
			nAnchor = n
		}
		if nAnchor != nil {
			if n != nAnchor { // don't write the a tag and its attributes
				html.Render(w, n)
			}
			if n.Type == html.TextNode {
				sTxt += n.Data
			}
		}
		for c := n.FirstChild; c != nil; c = c.NextSibling {
			f(c)
		}
		if n == nAnchor {
			fmt.Println("Text:", sTxt)
			fmt.Println("InnerHTML:", bufInnerHtml.String())
			sTxt = ""
			bufInnerHtml.Reset()
			nAnchor = nil
		}
	}
	f(doc)
}

func prepareInsert(table string, params map[string]interface{}) string {
	columns := len(params)
	fieldSlice := make([]string, 0, columns)
	for field, _ := range params {
		fieldSlice = append(fieldSlice, field)
	}
	fields := strings.Join(fieldSlice, ",")
	placeholders := prepareQueryPlaceholders(1, columns)
	return fmt.Sprintf(`INSERT INTO %s (%s) VALUES (%s) RETURNING %s`, table, fields, placeholders, fields)
}
func prepareQueryPlaceholders(start, quantity int) string {
	placeholders := make([]string, 0, quantity)
	end := start + quantity
	for i := start; i < end; i++ {
		placeholders = append(placeholders, strings.Join([]string{"$", strconv.Itoa(i)}, ""))
	}
	return strings.Join(placeholders, ",")
}

func DefaultRetryPolicy() func(ctx context.Context, resp *http.Response, err error) (bool, error) {
	return func(ctx context.Context, resp *http.Response, err error) (bool, error) {
		// do not retry on context.Canceled or context.DeadlineExceeded
		//fmt.Printf("jkajsuiohsd %v\n", ctx.Err())
		if ctx.Err() != nil {
			return false, ctx.Err()
		}
		if err != nil {
			if v, ok := err.(*url.Error); ok {
				// Don't retry if the error was due to too many redirects.
				if redirectsErrorRegex.MatchString(v.Error()) {
					return false, nil
				}
				// Don't retry if the error was due to an invalid protocol scheme.
				if schemeErrorRegex.MatchString(v.Error()) {
					return false, nil
				}
				// Don't retry if the error was due to TLS cert verification failure.
				if _, ok := v.Err.(x509.UnknownAuthorityError); ok {
					return false, nil
				}
			}
			// The error is likely recoverable so retry.
			return true, nil
		}
        //EXPECT HANDLING BASED ON STATUS CODES, BUT ABSENT
		return false, nil
	}
}

func baseRetryPolicy(resp *http.Response, err error) (bool, error) {
	if err != nil {
		if v, ok := err.(*url.Error); ok {
			// Don't retry if the error was due to too many redirects.
			if redirectsErrorRe.MatchString(v.Error()) {
				return false, v
			}
			// Don't retry if the error was due to an invalid protocol scheme.
			if schemeErrorRe.MatchString(v.Error()) {
				return false, v
			}
			// Don't retry if the error was due to TLS cert verification failure.
			if notTrustedErrorRe.MatchString(v.Error()) {
				return false, v
			}
			if _, ok := v.Err.(x509.UnknownAuthorityError); ok {
				return false, v
			}
		}
		// The error is likely recoverable so retry.
		return true, nil
	}
	// 429 Too Many Requests is recoverable. Sometimes the server puts
	// a Retry-After response header to indicate when the server is
	// available to start processing request from client.
	if resp.StatusCode == http.StatusTooManyRequests {
		return true, nil
	}
	// Check the response code. We retry on 500-range responses to allow
	// the server time to recover, as 500's are typically not permanent
	// errors and may relate to outages on the server side. This will catch
	// invalid response codes as well, like 0 and 999.
    //THIS PART HERE FLAGS RETRIES ON STATUS CODES!
	if resp.StatusCode == 0 || (resp.StatusCode >= 500 && resp.StatusCode != http.StatusNotImplemented) {
		return true, fmt.Errorf("unexpected HTTP status %s", resp.Status)
	}
	return false, nil
}

