type Responder struct {
	Encoder Encoder
	Before BeforeFunc
	After AfterFunc
	OnError OnErrorFunc
}
func (r *Responder) writeResponse(w http.ResponseWriter, v interface{}, status int) {
	if r.Before != nil {
		status, v = r.Before(w, v, status)
	}
	encoder := JSON
	if r.Encoder != nil {
		encoder = r.Encoder
	}
	w.Header().Set("Content-Type", encoder.ContentType())
	w.WriteHeader(status)
	if err := encoder.Encode(w, v); err != nil {
		if r.OnError != nil {
			r.OnError(err)
		}
	}
	if r.After != nil {
		r.After(v, status)
	}
}
func (r *Responder) Ok(w http.ResponseWriter, v interface{}) {
	r.writeResponse(w, v, http.StatusOK)
}

// provide pipes for two-way communication with the server
func setupIo(session *ssh.Session) (io.WriteCloser, chan []byte, chan []byte) {
	stdout, err := session.StdoutPipe()
	Expect(err).To(BeNil())
	stderr, err := session.StderrPipe()
	Expect(err).To(BeNil())
	stdin, err := session.StdinPipe()
	Expect(err).To(BeNil())
	stdoutChan := make(chan []byte)
	stderrChan := make(chan []byte)
	go readPipe(stdout, stdoutChan)
	go readPipe(stderr, stderrChan)
	return stdin, stdoutChan, stderrChan
}
func readPipe(pipe io.Reader, outputChan chan []byte) {
	b := make([]byte, 100)
	for {
		if n, err := pipe.Read(b); err != nil {
			return
		} else if n > 0 {
			outputChan <- b[:n]
		}
	}
}
