	fetch  c1 into c_id;
	fetch  c2 into c_val1;
	fetch c3 into c_val2;
	if fin=true then
		leave lp;
	end if;
	update t set id=c_id,val1=c_val1,val2=c_val2 where row_id=c_row_id;
end loop lp;
