func printGrid(grid [9][9]rune) {
	for i := range grid {
		for j := range grid {
			PrintRune(grid[i][j])
			PrintRune(' ')
		}
		PrintRune('\n')
	}
}
func isSafe(grid *[9][9]rune, row int, col int, num rune) bool {
	for x := 0; x < len(grid); x++ {
		if grid[row][x] == num || grid[x][col] == num {
			return false
		}
	}
	startRow := row - row%3
	startCol := col - col%3
	for i := 0; i < 3; i++ {
		for j := 0; j < 3; j++ {
			if grid[i+startRow][j+startCol] == num {
				return false
			}
		}
	}
	return true
}
func Sudoku(grid *[9][9]rune, row int, col int) bool {
	if col == 9 && row == 8 {
		return true
	}
	if col == 9 {
		row++
		col = 0
	}
	if grid[row][col] > '0' {
		return Sudoku(grid, row, col+1)
	}
	for num := '1'; num <= '9'; num++ {
		if isSafe(grid, row, col, num) {
			grid[row][col] = num
			if Sudoku(grid, row, col+1) {
				return true
			}
		}
		grid[row][col] = '0'
	}
	return false
}
func PrintRune(r rune) error {
	l := utf8.RuneLen(r)
	if l == -1 {
		return errors.New("The rune is not a valid value to encode in UTF-8")
	}
	p := make([]byte, l)
	utf8.EncodeRune(p, r)
	_, err := os.Stdout.Write(p)
	return err
}
