import (
	"fmt"
	"reflect"
)
type Repo struct {
	Key         string `json:"key"`
	Type        string `json:"type"`
	PackageType string `json:"packageType"`
}
var someDatas = []Repo{
	{
		Key:         "test",
		Type:        "test",
		PackageType: "test",
	},
	{
		Key:         "test1",
		Type:        "test1",
		PackageType: "test1",
	},
	{
		Key:         "test",
		Type:        "LOCAL",
		PackageType: "test23",
	},
}
func getWantedRepos(conf map[string]interface{}) []Repo {
	var returned []Repo
	for _, val := range someDatas {
		flag := true
		for k := range conf {
			if !reflect.DeepEqual(reflect.ValueOf(val).FieldByName(k).Interface(), conf[k]) {
				flag = false
				break
			}
		}
		if flag {
			returned = append(returned, val)
		}
	}
	return returned
}
func main() {
	var repos []Repo
	repos = getWantedRepos(map[string]interface{}{
		"Key": "test",
	})
	fmt.Println("1 ============================")
	fmt.Printf("%+v\n", repos)
	repos = getWantedRepos(map[string]interface{}{
		"Type": "LOCAL",
	})
	fmt.Println("2 ============================")
	fmt.Printf("%+v\n", repos)
	repos = getWantedRepos(map[string]interface{}{
		"Type":        "LOCAL",
		"PackageType": "test23",
	})
	fmt.Println("3 ============================")
	fmt.Printf("%+v\n", repos)
	repos = getWantedRepos(map[string]interface{}{
		"Type":        "LOCAL",
		"PackageType": "NONE",
	})
	fmt.Println("4 ============================")
	fmt.Printf("%+v\n", repos)
}

