import (
	"fmt"
	"sync"
)
func printOddNumber(chOdd chan int, chEven chan int, wg *sync.WaitGroup, cnt int) {
	defer wg.Done()
	for {
		num := <-chOdd
		if num == cnt {
			chEven <- num
			return
		}
		fmt.Printf("Odd Number:%d\n", num)
		num++
		chEven <- num
	}
}
func printEvenNumber(chEven chan int, chOdd chan int, wg *sync.WaitGroup, cnt int) {
	defer wg.Done()
	num := 1
	chOdd <- num
	for {
		num := <-chEven
		fmt.Printf("Even Number:%d\n", num)
		if num == cnt {
			chOdd <- num
			return
		}
		num++
		chOdd <- num
	}
}
func main() {
	chOdd := make(chan int, 1)
	chEven := make(chan int, 1)
	var wg sync.WaitGroup
	wg.Add(2)
	cnt := 20
	go printOddNumber(chOdd, chEven, &wg, cnt)
	go printEvenNumber(chEven, chOdd, &wg, cnt)
	wg.Wait()
	close(chOdd)
	close(chEven)
}

