import (
	"fmt"
	"sync"
	"sync/atomic"
)
func main() {
	var done int32
	syncChannel := make(chan bool) // unbuffered channel.
	wg := new(sync.WaitGroup)
	wg.Add(2)
	go func() {
		// prints even numbers.
		defer wg.Done()
		for i := 0; i < 50; i += 2 {
			<-syncChannel
			fmt.Printf("Even: %d\n", i)
			syncChannel <- true
		}
		atomic.StoreInt32(&done, 1)
	}()
	syncChannel <- true
	go func() {
		// prints odd numbers.
		defer wg.Done()
		for i := 1; i < 50; i += 2 {
			<-syncChannel
			fmt.Printf("Odd:%d\n", i)
			if atomic.LoadInt32(&done) != 0 {
				return
			}
			syncChannel <- true
		}
	}()
	wg.Wait()
	close(syncChannel)
}

