)
func main() {
	addrStrs := []string{
		"35.132.199.128/27", "8.44.144.248/32", "87.117.185.193", "45.23.45.45",
	}
	trie := ipaddr.AddressTrie{}
	for _, addrStr := range addrStrs {
		addr := ipaddr.NewIPAddressString(addrStr).GetAddress().ToAddressBase()
		trie.Add(addr)
	}
	fmt.Println("The trie is", trie)
	addrSearchStrs := []string{
		"35.132.199.143", "8.44.144.248", "45.23.45.45", "127.0.0.1",
	}
	for _, addrStr := range addrSearchStrs {
		addr := ipaddr.NewIPAddressString(addrStr).GetAddress().ToAddressBase()
		triePath := trie.ElementsContaining(addr)
		if triePath.Count() > 0 {
			fmt.Println("The blocks and addresses containing", addr, "are",
			triePath)
		} else {
			fmt.Println("No blocks nor addresses contain", addr)
		}
	}
}

func (d *Node) GobEncode() ([]byte, error) {
    var buf bytes.Buffer
    encoder := gob.NewEncoder(&buf)
    if err := encoder.Encode(d.value); err != nil {
        return nil, err
    }
    if d.next != nil {
        if err := encoder.Encode(d.next); err != nil {
            return nil, err
        }
    }
    return buf.Bytes(), nil
}
func (d *Node) GobDecode(b []byte) error {
    buf := bytes.NewBuffer(b)
    decoder := gob.NewDecoder(buf)
    if err := decoder.Decode(&d.value); err != nil {
        return err
    }
    if buf.Len() != 0 {
        return decoder.Decode(&d.next)
    }
    return nil
}

func encodePointer(encoder *gob.Encoder, ptr any) error {
    isNil := ptr == nil || (reflect.ValueOf(ptr).Kind() == reflect.Ptr && reflect.ValueOf(ptr).IsNil())
    if err := encoder.Encode(isNil); err != nil {
        return err
    }
    if isNil {
        return nil
    }
    return encoder.Encode(ptr)
}

func decodePointer(decoder *gob.Decoder, ptr any) error {
    var isNil bool
    err := decoder.Decode(&isNil)
    if err != nil {
        return err
    }
    if isNil {
        return nil
    }
    return decoder.Decode(ptr)
}

