func NewSqlxConn(driverName, dataSourceName string) (IStorage, error) {
	db, err := sqlx.Connect(driverName, dataSourceName)
	if err != nil {
		return nil, err
	}
	return &sqlxConn{DB: db}, nil
}
func (s *sqlxConn) GetUsers(orderBy string) ([]*User, error) {
	getUsersQuery := "SELECT * FROM users"
	// Add the order by if it has been passed though
	if orderBy != "" {
		getUsersQuery += " ORDER BY " + orderBy + " ASC"
	}
	users := make([]*User, 0)
	s.Select(&users, getUsersQuery)
	if len(users) == 0 {
		return users, nil
	}
	// Find the address for each user
	var uuids []string
	for _, user := range users {
		uuids = append(uuids, user.Uuid)
	}
	addresses := make([]*Address, 0)
	s.Select(&addresses, "select * from addresses where user_uuid IN ("+strings.Join(uuids, ",")+")")
	for _, address := range addresses {
		for _, user := range users {
			user.Address = address
		}
	}
	return users, nil
}
