package main
import (
	"bufio"
	"log"
	"io"
	"os/exec"
)
func request(r *bufio.Reader, w io.Writer, str string) string {
	w.Write([]byte(str))
	w.Write([]byte("\n"))
	str, err := r.ReadString('\n')
	if err != nil {
		panic(err)
	}
	return str[:len(str)-1]
}
func main() {
	cmd := exec.Command("bash", "menu.sh")
	inr, inw := io.Pipe()
	outr, outw := io.Pipe()
	cmd.Stdin = inr
	cmd.Stdout = outw
	if err := cmd.Start(); err != nil {
		panic(err)
	}
	go cmd.Wait()
	reader := bufio.NewReader(outr)
	log.Printf(request(reader, inw, "Tom"))
	log.Printf(request(reader, inw, "Rose"))
}

func newCompressMiddleware(level int) func(next http.Handler) http.Handler {
	compressor := middleware.NewCompressor(level)
	// brotli encoder
	compressor.SetEncoder("br", func(w io.Writer, level int) io.Writer {
		brWriter, err := brotli.NewWriterLevel(w, level)
		if err != nil {
			return nil
		}
		return brWriter
	})
	// zstd encoder
	compressor.SetEncoder("zstd", func(w io.Writer, level int) io.Writer {
		zstdWriter, err := zstd.NewWriter(w, zstd.WithEncoderLevel(zstd.SpeedDefault))
		if err != nil {
			return nil
		}
		return zstdWriter
	})
	return compressor.Handler
}

