import (
	"context"
	"fmt"
	"math/rand"
	"sync"
	"time"
)
type result struct {
	sync.RWMutex
	max uint64
}
func findMax(ctx context.Context, workers int) uint64 {
	var (
		res = result{}
		wg  = sync.WaitGroup{}
	)
	for i := 0; i < workers; i++ {
		select {
		case <-ctx.Done():
			// RLock to read res.max
			res.RLock()
			ret := res.max
			res.RUnlock()
			return ret
		default:
			wg.Add(1)
			go func() {
				defer wg.Done()
				num := compute()
				// Lock so that read from res.max and write
				// to res.max is safe. Else, data race could
				// occur.
				res.Lock()
				if num > res.max {
					res.max = num
				}
				res.Unlock()
			}()
		}
	}
	// Wait for all the goroutine to finish work i.e., all
	// workers are done computing and updating the max.
	wg.Wait()
	return res.max
}
func compute() uint64 {
	rnd := rand.Int63n(100)
	time.Sleep(time.Duration(rnd) * time.Millisecond)
	return rand.Uint64()
}
func main() {
	maxDuration := 2 * time.Second
	concurrency := 10
	ctx, cancel := context.WithTimeout(context.Background(), maxDuration)
	defer cancel()
	fmt.Println(findMax(ctx, concurrency))
}

