func (rr *rot13Reader) Read(b []byte) (n int, err error) { // <-- this 'err'
    rb := make([]byte, 8)
    var ttl int
    for {
        n, err := rr.r.Read(rb) // <-- and this 'err' are different
        if err == io.EOF {
            return ttl, io.EOF
            // break <----------------------------here's the problem
        } else if err != nil {
            panic(err)
        } else {
            for i, c := range rb[:n] {
                b[i+ttl] = decodeRot13(c)
            }
            ttl += n
        }
    }
    return ttl, err
}

