		defer fmt.Println(fmt.Println("a1")) // The "a1" will print between a and b (note `3 nil` will be printed later, this is the return from the inner fmt.Println)
		defer fmt.Println(recover())         // catch panic(2)
		fmt.Println("b")
		defer panic(1)
		fmt.Println("c")
		recover() // no-op - B, should be called inside deferred function,
	}()
	defer recover() // no-op - A, should be called inside deferred function,
	panic(2)
	// Output:
	// a
	// a1
	// b
	// c
	// 2
	// 3 <nil>
	// 1
}

type SomeType struct {
	Name string    `json:"name"`
	Time time.Time `json:"someTime"`
}
func (st *SomeType) UnmarshalJSON(data []byte) error {
	type parseType struct {
		Name string `json:"name"`
		Time string `json:"someTime"`
	}
	var res parseType
	if err := json.Unmarshal(data, &res); err != nil {
		return err
	}
	parsed, err := time.Parse("15:04:05", res.Time)
	if err != nil {
		return err
	}
	now := time.Now()
	st.Name = res.Name
	st.Time = time.Date(now.Year(), now.Month(), now.Day(), parsed.Hour(), parsed.Minute(), parsed.Second(), 0, now.Location())
	return nil
}

func getIPSetInfo(ipsetIds []string) []IpsetInfo {
    mu := &sync.Mutex{}
    var wg sync.WaitGroup

    var result []IpsetInfo

    call := func(val string){
        defer wg.Done()

        url := "https://api-cloud-platform.com/cloudapi/1.0.0/firewallGroups/" + val
        _, body := httpGet(url, map[string]string{
            "Authorization": "Bearer " + accessToken,
        })
        var x IpsetInfo
        json.Unmarshal([]byte(body), &x)

        mu.Lock()
        defer mu.Unlock()

        result = append(result, x)
    }

    for _, val := range ipsetIds {
        wg.Add(1)
        go call(val)
    }
    wg.Wait()
    return result
}

