import "testing"
func BenchmarkMerge1(b *testing.B) {
	for i := 0; i < b.N; i++ {
		num1 := []int{1, 2, 3}
		num2 := []int{4, 5, 6}
		merge1(num1, len(num1), num2, len(num2))
	}
}
func merge1(nums1 []int, m int, nums2 []int, n int) {
	tmpSlice := make([]int, m+n)
	tmpIndex := 0
	index1 := 0
	index2 := 0
	for index1 < m {
		value1 := nums1[index1]
		for index2 < n {
			value2 := nums2[index2]
			if value1 <= value2 {
				break
			} else {
				tmpSlice[tmpIndex] = value2 // <-- Assign
				index2++
				tmpIndex++
			}
		}
		tmpSlice[tmpIndex] = value1 // <-- Assign
		index1++
		tmpIndex++
	}
	copy(nums1, tmpSlice[:tmpIndex])
	copy(nums1[tmpIndex:], nums2[index2:])
}
func BenchmarkMerge2(b *testing.B) {
	for i := 0; i < b.N; i++ {
		num1 := []int{1, 2, 3}
		num2 := []int{4, 5, 6}
		merge2(num1, len(num1), num2, len(num2))
	}
}
func merge2(nums1 []int, m int, nums2 []int, n int) {
	tmpSlice := make([]int, 0, m+n)
	tmpIndex := 0
	index1 := 0
	index2 := 0
	for index1 < m {
		value1 := nums1[index1]
		for index2 < n {
			value2 := nums2[index2]
			if value1 <= value2 {
				break
			} else {
				tmpSlice = append(tmpSlice, value2) // <-- Append
				index2++
				tmpIndex++
			}
		}
		tmpSlice = append(tmpSlice, value1) // <-- Append
		index1++
		tmpIndex++
	}
	copy(nums1, tmpSlice[:tmpIndex])
	copy(nums1[tmpIndex:], nums2[index2:])
}

type ErrorHook struct {
}
func (h *ErrorHook) Levels() []logrus.Level {
    // fire only on ErrorLevel (.Error(), .Errorf(), etc.)
	return []logrus.Level{logrus.ErrorLevel}
}
func (h *ErrorHook) Fire(e *logrus.Entry) error {
    // e.Data is a map with all fields attached to entry
	if _, ok := e.Data["severity"]; !ok {
		e.Data["severity"] = "normal"
	}
	return nil
}
