package main
import (
	"fmt"
	"math/rand"
	"sync"
	"time"
)
type pair struct {
	row, col int
}
const length = 1000
var start time.Time
var rez [length][length]int
func main() {
	const threadlength = 1
	pairs := make(chan pair, 1000)
	var wg sync.WaitGroup
	var a [length][length]int
	var b [length][length]int
	for i := 0; i < length; i++ {
		for j := 0; j < length; j++ {
			a[i][j] = rand.Intn(10)
			b[i][j] = rand.Intn(10)
		}
	}
	wg.Add(threadlength)
	for i := 0; i < threadlength; i++ {
		go Calc(pairs, &a, &b, &rez, &wg)
	}
	start = time.Now()
	for i := 0; i < length; i++ {
		for j := 0; j < length; j++ {
			pairs <- pair{row: i, col: j}
		}
	}
    close(pairs)
	wg.Wait()
	elapsed := time.Since(start)
	fmt.Println("Binomial took ", elapsed)
	for i := 0; i < length; i++ {
		for j := 0; j < length; j++ {
			fmt.Print(rez[i][j])
			fmt.Print(" ")
		}
		fmt.Println(" ")
	}
}
func Calc(pairs chan pair, a, b, rez *[length][length]int, wg *sync.WaitGroup) {
	for {
		pair, ok := <-pairs
		if !ok {
			break
		}
		rez[pair.row][pair.col] = 0
		for i := 0; i < length; i++ {
			rez[pair.row][pair.col] += a[pair.row][i] * b[i][pair.col]
		}
	}
	wg.Done()
}

func (fc *FirebaseClient) ReadCollection(collectionPath string, objects interface{}) error {
	ctx := context.Background()
	opt := option.WithCredentialsJSON([]byte(os.Getenv("FIREBASE_CREDENTIALS")))
	client, err := firestore.NewClient(ctx, os.Getenv("FIREBASE_PROJECT_ID"), opt)
	if err != nil {
		return err
	}
	defer client.Close()
	collectionRef := client.Collection(collectionPath)
	docs, err := collectionRef.DocumentRefs(ctx).GetAll()
	if err != nil {
		return err
	}
	log.Printf("Total documents: %i", len(docs))

	dest := reflect.ValueOf(objects).Elem()
	log.Printf("dest: %v", dest)
	for _, doc := range docs {
		docsnap, err := doc.Get(ctx)
		if err != nil {
			return err
		}
		obj := reflect.New(dest.Type().Elem())
		if err := docsnap.DataTo(obj.Interface()); err != nil {
			return err
		}
		log.Printf("obj: %v", obj)
		dest = reflect.Append(dest, obj.Elem())


	}
	reflect.ValueOf(objects).Elem().Set(dest)
	log.Printf("objects: %v", dest)
	return nil
}

