import (
	"bufio"
	"context"
	"log"
	"os"
	"os/exec"
	"sync"
	"syscall"
	"time"
)
func main() {
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
	cmd := exec.CommandContext(ctx, "sh", "-c", "for i in $(seq 1 10); do echo $i; sleep 0.25; done")
	cmd.Stderr = os.Stderr
	stdout, err := cmd.StdoutPipe()
	if err != nil {
		log.Fatal(err)
	}
	defer stdout.Close()
	scanner := bufio.NewScanner(stdout)
	if err := cmd.Start(); err != nil {
		log.Fatal(err)
	}
	go func() { time.Sleep(1 * time.Second); syscall.Kill(cmd.Process.Pid, syscall.SIGKILL) }()
	var wg sync.WaitGroup
	wg.Add(1)
	go func() {
		defer wg.Done()
		for scanner.Scan() {
			log.Print(scanner.Text())
		}
		if scanner.Err() != nil {
			log.Printf("Error reading stdout, %v", scanner.Err())
		}
	}()
	done := make(chan error, 1)
	go func() {
		done <- cmd.Wait()
	}()
	select {
	case <-ctx.Done():
		log.Fatal(ctx.Err())
	case err := <-done:
		if err != nil {
			log.Fatal(err)
		}
	}
	wg.Wait()
}

type hashReader struct {
	// If h != nil, then r is the original reader,
	// else r is a reader on the hash bytes.
	r io.Reader
	// h is set to nil when original reader is read
	// to EOF.
	h hash.Hash
}
func (hr *hashReader) Read(b []byte) (int, error) {
	n, err := hr.r.Read(b)
	// Hash the bytes when reading the main stream.
	if hr.h != nil {
		hr.h.Write(b[:n])
		// If we reached the end of the main stream, swap in
		// a reader on the hash bytes.
		if err == io.EOF {
			sum := hr.h.Sum(nil)
			hr.r = bytes.NewReader(sum[:])
			hr.h = nil
			err = nil
		}
	}
	return n, err
}

var (
	_ Expr = new(Literal)
	_ Expr = new(Grouping)
	_ Expr = new(Unary)
	_ Expr = new(Binary)
)
type Expr interface {
	Evaluate() string
}
type Literal struct {
	Value string
}
func (l *Literal) Evaluate() string {
	return l.Value
}
type Grouping struct {
	Expr Expr
}
func (g *Grouping) Evaluate() string {
	if g.Expr == nil {
		return ""
	} else {
		return fmt.Sprintf("(group %s)", g.Expr.Evaluate())
	}
}
type Unary struct {
	Token *Token
	Right Expr
}
func (u *Unary) Evaluate() string {
	if u.Right == nil {
		return ""
	} else {
		token := ""
		if u.Token != nil {
			token = u.Token.Data
		}
		return fmt.Sprintf("(%s %s)", token, u.Right.Evaluate())
	}
}
type Binary struct {
	Left  Expr
	Token *Token
	Right Expr
}
type A struct {
	a *A
}
func (b *Binary) Evaluate() string {
	if b.Left == nil || b.Right == nil || b.Token == nil {
		return ""
	} else {
		return fmt.Sprintf("(%s %s %s)", b.Token.Data, b.Left.Evaluate(), b.Right.Evaluate())
	}
}

