    if err != nil {
        return store.User{}, fmt.Errorf("expression build: %w", err)
    }
    res, err := d.Client.Query(ctx, &dynamodb.QueryInput{
        TableName: aws.String(d.Table),
        IndexName: aws.String(d.GlobalIndex),
        ExpressionAttributeNames:  expr.Names(),
        ExpressionAttributeValues: expr.Values(),
        KeyConditionExpression:    expr.Condition(),
    })

func WithChannels() {
    var counter int
    ch := make(chan int)
    var wg sync.WaitGroup
    for i := 0; i < 10; i++ {
        wg.Add(1)
        go func() {
            defer wg.Done()
            for j := 0; j < 1000; j++ {
                time.Sleep(time.Duration(1))
                ch <- 1
            }
        }()
    }
    // Use a goroutine to wait for all the senders to complete and then close the channel.
    go func() {
        wg.Wait()
        close(ch)
    }()
    // Receive from the channel until it's closed.
    for {
        increment, ok := <-ch
        if !ok {
            // channel closed, break
            break
        }
        counter += increment
    }

    fmt.Println("Expected counter: 10000, Actual counter:", counter)
}

