var wg sync.WaitGroup
func main() {
	linesChan := make(chan (logEntry))
	// Process entries from lines
	go func() {
		for c := range linesChan {
			time.Sleep(100 * time.Millisecond)
			fmt.Printf("%v\n", c)
		}
	}()
	// Read lines
	for i := 1; i <= 10; i++ {
		wg.Add(1)
