	"strconv"
	"strings"
)
type MyFloat64 float64
func (f *MyFloat64) UnmarshalJSON(data []byte) error {
	raw := string(data)
	raw = strings.TrimPrefix(raw, "\"")
	raw = strings.TrimSuffix(raw, "\"")
	if parsedFloat, err := strconv.ParseFloat(raw, 64); err != nil {
		return err
	} else {
		*f = MyFloat64(parsedFloat)
		return nil
