				for _, i := range helper(n) {
					ch <- i
				}
				return nil
			})
		}
		if err := g.Wait(); err != nil {
			panic(err)
		}
		close(ch)
	}()
	out := make([]int, 0)
	for i := range ch {
		out = append(out, i)
	}
	fmt.Println(out) // should have the same elements as [0 1 0 1 2]
}
func helper(n int) []int {
	out := make([]int, 0)
	for i := 0; i < n; i++ {
		out = append(out, i)
	}
	return out
}

