import (
	"fmt"
	"math/rand"
	"sync"
	"time"
)
const (
	numOfPhilosophers = 5
	numOfMeals        = 3
	maxEaters         = 2
)
func main() {
	chopsticks := make([]sync.Mutex, 5)
	permissionChannel := make(chan bool)
	finishEating := make(chan bool)
	go permissionFromHost(permissionChannel, finishEating)
	var wg sync.WaitGroup
	wg.Add(numOfPhilosophers)
	for i := 1; i <= numOfPhilosophers; i++ {
		go eat(i, &chopsticks[i-1], &chopsticks[i%numOfPhilosophers], &wg, permissionChannel, finishEating)
	}
	wg.Wait()
}
func eat(philosopherId int, left *sync.Mutex, right *sync.Mutex, wg *sync.WaitGroup, permissionChannel <-chan bool, finishEatingChannel chan<- bool) {
	defer wg.Done()
	for i := 1; i <= numOfMeals; i++ {
		//lock chopsticks in random order
		if RandBool() {
			left.Lock()
			right.Lock()
		} else {
			right.Lock()
			left.Lock()
		}
		fmt.Printf("waiting for permission from host %d\n", philosopherId)
		<-permissionChannel
		fmt.Printf("starting to eat %d (time %d)\n", philosopherId, i)
		fmt.Printf("finish to eat %d (time %d)\n", philosopherId, i)
		//release chopsticks
		left.Unlock()
		right.Unlock()
		//let host know I am done eating
		finishEatingChannel <- true
	}
}
func permissionFromHost(permissionChannel chan<- bool, finishEating <-chan bool) {
	ctr := 0
	for {
		if ctr < maxEaters {
			select {
			case <-finishEating:
				ctr--
			case permissionChannel <- true:
				ctr++
			}
		} else {
			<-finishEating
			ctr--
		}
	}
}
func RandBool() bool {
	rand.Seed(time.Now().UnixNano())
	return rand.Intn(2) == 1
}

// type definition adapted from https://www.onlinetool.io/xmltogo/
type KML struct {
	XMLName xml.Name `xml:"kml"`
	Text    string   `xml:",chardata"`
	XMLNS   string   `xml:"xmlns,attr"`
	GX      string   `xml:"gx,attr"`
	KML     string   `xml:"kml,attr"`
	Atom    string   `xml:"atom,attr"`
	Folder  struct {
		Text     string `xml:",chardata"`
		Name     string `xml:"name"`
		Open     string `xml:"open"`
		Document struct {
			Text      string `xml:",chardata"`
			Name      string `xml:"name"`
			Placemark struct {
				Text  string `xml:",chardata"`
				Style struct {
					Text      string `xml:",chardata"`
					LineStyle struct {
						Text  string `xml:",chardata"`
						Color string `xml:"color"`
						Width string `xml:"width"`
					} `xml:"LineStyle"`
					PolyStyle struct {
						Text    string `xml:",chardata"`
						Color   string `xml:"color"`
						Fill    string `xml:"fill"`
						Outline string `xml:"outline"`
					} `xml:"PolyStyle"`
				} `xml:"Style"`
				LineString struct {
					Text        string `xml:",chardata"`
					Tessellate  string `xml:"tessellate"`
					Coordinates string `xml:"coordinates"`
				} `xml:"LineString"`
			} `xml:"Placemark"`
		} `xml:"Document"`
	} `xml:"Folder"`
}

func readKML(filename string) (*KML, error) {
  f, err := os.Open(filename)
  if err != nil {
    return nil, fmt.Errorf("opening KML file: %w", err) // contains filename
  }
  defer f.Close() // reading, ignoring error is acceptable
  var kml KML
  if err := xml.NewDecoder(f).Decode(&kml); err != nil {
    return nil, fmt.Errorf("decoding XML from %q as KML: %w", filename, err)
  }
  return &kml, nil
}
func writeKML(filename string, kml *KML) error {
  f, err := os.Create(filename)
  if err != nil {
    return fmt.Errorf("creating KML file: %w", err) // contains filename
  }
  defer f.Close() // double close is OK for *os.File
  enc := xml.NewEncoder(f)
  enc.Indent("", "    ")
  if err := enc.Encode(kml); err != nil {
    return nil, fmt.Errorf("encoding KML to %q: %w", filename, err)
  }
  return nil
}

