type CustomJSONType struct {
	data interface{}
}
func (t *CustomJSONType) Scan(src interface{}) error {
	bytes, ok := src.([]byte)
	if !ok {
		return errors.New("invalid input type")
	}
	err := json.Unmarshal(bytes, t.data)
	if err != nil{
		return err
	}
	return nil
}
