	ctx, cancel := context.WithCancel(context.Background())
	tasks := make(chan struct{})
	go func() {
		time.Sleep(1 * time.Second) // a job
		tasks <- struct{}{}
	}()
	go func() {
		time.Sleep(3 * time.Second) // a job
		cancel()
	}()
	for {
		select {
		case <-ctx.Done():
			log.Println("QUIT")
			return
		case <-tasks:
