#include <cryptopp/files.h>
using CryptoPP::FileSource;
using CryptoPP::FileSink;
#include <cryptopp/cryptlib.h>
using CryptoPP::PrivateKey;
using CryptoPP::PublicKey;
using CryptoPP::BufferedTransformation;
#include <cryptopp/sha.h>
#include <cryptopp/rsa.h>
#include <cryptopp/asn.h>
using CryptoPP::RSA;
#include <cryptopp/base64.h>
using CryptoPP::Base64Encoder;
using CryptoPP::Base64Decoder;
#include <cryptopp/osrng.h>
using CryptoPP::AutoSeededRandomPool;
using namespace std;
int main() {
    ByteQueue queue;
    string RSA_PUBLIC_KEY ="-----BEGIN PUBLIC KEY----------END PUBLIC KEY-----";
    static string HEADER = "-----BEGIN PUBLIC KEY-----";
    static string FOOTER = "-----END PUBLIC KEY-----";
    size_t pos1 = RSA_PUBLIC_KEY.find(HEADER);
    if(pos1 == string::npos) throw runtime_error("PEM header not found");
    size_t pos2 = RSA_PUBLIC_KEY.find(FOOTER, pos1+1);
    if(pos2 == string::npos) throw runtime_error("PEM footer not found");
    // Start position and length
    pos1 = pos1 + HEADER.length();
    pos2 = pos2 - pos1;
    string keystr = RSA_PUBLIC_KEY.substr(pos1, pos2);
    CryptoPP::StringSource ss{keystr.c_str(), true};
    Base64Decoder decoder;
    decoder.Attach(new Redirector(queue));
    ss.TransferTo(decoder);
    decoder.MessageEnd();
    cout << keystr << endl;
    try {
        RSA::PublicKey public_key;
        if(queue.IsEmpty()) {
            cerr << "The queue is empty...";
        }
        public_key.BERDecode(queue);
        AutoSeededRandomPool prng;
        bool valid = public_key.Validate(prng, 3);
        if(!valid) cerr << "RSA public key is not valid" << endl;
        cout << "N:" << public_key.GetModulus() << endl;
        cout << "E:" << public_key.GetPublicExponent() << endl;
    } catch (exception& e) {
        printf( "Caught exception: %s\n", e.what() );
        exit (1);
    }
}

package main
import "fmt"
func main() {
    channelList := make([]chan int, 0, 2)
    FChannel := make(chan int, 5)
    for i := 1; i <= 5; i++ {
        FChannel <- i // contain values from 1 to 5
    }
    close(FChannel)
    channelList = append(channelList, FChannel)
    SChannel := make(chan int, 5)
    for j := 6; j <= 10; j++ {
        SChannel <- j // contain values from 6 to 10
    }
    close(SChannel)
    channelList = append(channelList, SChannel)
    for _, c := range channelList {
        for v := range c {
            fmt.Println(v)
        }
    }
}

package main
import (
	"bytes"
	"encoding/xml"
	"fmt"
	"io"
	"log"
	"strings"
)
const data = `<?xml version="1.0" encoding="ASCII"?>
<QuestionFormAnswers xmlns="http://mechanicalturk.amazonaws.com/AWSMechanicalTurkDataSchemas/2005-10-01/QuestionFormAnswers.xsd">
  <Answer>
    <QuestionIdentifier>annotatedResult.boundingBoxes</QuestionIdentifier>
    <FreeText>[{"height":641,"label":"F-22 Raptor","left":82,"top":97,"width":1088}]</FreeText>
  </Answer>
  <Answer>
    <QuestionIdentifier>annotatedResult.inputImageProperties.height</QuestionIdentifier>
    <FreeText>839</FreeText>
  </Answer>
  <Answer>
    <QuestionIdentifier>annotatedResult.inputImageProperties.width</QuestionIdentifier>
    <FreeText>1260</FreeText>
  </Answer>
</QuestionFormAnswers>`
func main() {
	type DimensionInfo struct {
		FreeText []string `xml:"Answer>FreeText"`
	}
	var dec = xml.NewDecoder(strings.NewReader(data))
	dec.CharsetReader = func(charset string, input io.Reader) (io.Reader, error) {
		switch strings.ToLower(charset) {
		case "ascii", "utf-8":
			return input, nil
		default:
			return nil, fmt.Errorf("cannot handle XML encoding: %s", charset)
		}
	}
	var t DimensionInfo
	err := dec.Decode(&t)
	if err != nil {
		log.Fatal(err)
	}
	fmt.Println(t.FreeText)
}

func returnError(w http.ResponseWriter, err error, code int) {
	var errorData Error
	output, err := json.Marshal(&errorData)
	if err != nil {
		w.Write([]byte("Cannot serialize error"))
		return
	}
	w.Header().Set("Content-Type", "application/json")
	w.WriteHeader(code)
	w.Write(output)
}
func parseRequest(r *http.Request) (Trade, error) {
	return Trade{}, nil
}
func saveTrade(t Trade) (TradeSubmitted, error) {
	return TradeSubmitted{}, nil
}
func createTrade(w http.ResponseWriter, r *http.Request) {
	trade, err := parseRequest(r)
	if err != nil {
		returnError(w, err, 400)
		return
	}
	tradeSubmitted, err := saveTrade(trade)
	if err != nil {
		returnError(w, err, 500)
		return
	}
	output, err := json.Marshal(&tradeSubmitted)
	if err != nil {
		err = fmt.Errorf("Cannot serialize Trade with id: xxx: %w", err)
		returnError(w, err, 500)
		return
	}
	w.Header().Set("Content-Type", "application/json")
	w.WriteHeader(http.StatusOK)
	w.Write(output)
}
