from numba import njit, prange
@njit(parallel=True)
def get_values(values):
    out = np.zeros_like(values, dtype=np.float64)
    for i in prange(len(values)):
        idx = np.int64(i)
        v = values[idx]
        while idx > -1 and values[idx] <= v:
            idx -= 1
        if idx > -1:
            out[i] = i - idx
    out[0] = np.nan
    return out
