import React from 'react';
import './App.css';
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {count: '?'};
    const nf = new Intl.NumberFormat(navigator.language, {minimumIntegerDigits: 6});
    this.receive = event => {
      const { data: count } = event;
      if (typeof count === 'number') {
        this.setState({count: nf.format(count)});
      }
    };
  }
  componentDidMount() {
    this.props.worker.addEventListener('message', this.receive);
  }
  componentWillUnmount() {
    this.props.worker.removeEventListener('message', this.receive);
  }
  render() {
    return (
      <p className="App">{this.state.count}</p>
    );
  }
}
export default App;

// App.js with hooks
import React, { useState, useEffect } from 'react';
import './App.css';
function App({ worker }) {
  const [count, setCount] = useState('?');
  const subscribe = () => {
    return listenForUpdates(worker, setCount);
  }
  useEffect(subscribe, [worker]);
  return (
      <p className="App">{ count }</p>
  );
}
function listenForUpdates(worker, setCount) {
  const nf = new Intl.NumberFormat(
    navigator.language,
    {minimumIntegerDigits: 6}
  );
  const receive = ({ data })  => {
    if (typeof data === 'number') {
      setCount(nf.format(data));
    }
  };
  const cleanup = () => {
    worker.removeEventListener('message', receive)
  };
  worker.addEventListener('message', receive);
  return cleanup;
}
export default App;

// file: registrationStatus.js
import { useState, useEffect } from 'react';
let current = 'Unchanged';
const subscriptions = new Set();
function subscribe(setStatus) {
  subscriptions.add(setStatus);
  return () => {
    subscriptions.delete(setStatus);
  };
}
function dispatch(status) {
  current = status;
  subscriptions.forEach((setStatus) => setStatus(status));
}
function useStatus() {
  const [status, setStatus] = useState(current);
  useEffect(() => subscribe(setStatus), []);
  return status;
}
// Updated but waiting for stale tabs to close
function onUpdate(_registration) {
  dispatch('Updated');
}
// Updated and running
function onSuccess(_registration) {
  dispatch('Successful');
}
export { useStatus, onUpdate, onSuccess };

// TimerContext.tsx
const TimerContext = React.createContext();
const TimeProvider = (props) => {
    const [time, setTime] = useState(0);
    const run = true;
    useEffect(()=>{
        if (run=== true && time < 1440){
            var timerID = setInterval(() => tick(time),1000);
            return function cleanup(){
                clearInterval(timerID);
            };
        }
    },[run, time]);
    function tick(time){
        setTime(time + 10);
        console.log(time);
    }
    const value = {
        run,
        time
    }
    return <TimerContext.Provider value={value} {...props} />
}
const useTimer = () => useContext(TimerContext)

class VerifyCaptchaMixin:
    """ Verify post requests which use google recaptcha """
    def post(self, request, *args, **kwargs):
        """
        Send a verification request to google if we can.
        """
        form_class = self.get_form_class()
        form = self.get_form(form_class)
        ''' Begin reCAPTCHA validation '''
        recaptcha_response = request.POST.get('g-recaptcha-response')
        if recaptcha_response:
            # captcha verification
            data = {
                'response': recaptcha_response,
                'secret': settings.NORECAPTCHA_SECRET_KEY
            }
            resp = requests.post(
                'https://www.google.com/recaptcha/api/siteverify',
                data=data
            )
            result_json = resp.json()
            ''' End reCAPTCHA validation '''
            if not result_json.get('success'):
                err_msg = "An error has occurred with Google reCAPTCHA"
                form.add_error(None, err_msg)
                # error in recaptcha, re-render the signup form
                return render(
                    request, self.template_name, {
                        'form': form,
                        'is_robot': True
                    }
                )
        return super().post(request, *args, **kwargs)

