interface LayoutWrapperProps {
  onMount: () => void;
}
const LayoutWrapper: React.FC<LayoutWrapperProps> = ({ onMount, children }) => {
  React.useEffect(() => {
    onMount();
  }, [onMount]);
  return <>{children}</>;
};
const Layout: React.FC = ({ children }) => {
  const references = React.useRef<HTMLElement[]>([]);
  React.useEffect(() => {
    references.current = [];
  });
  function getReference(ref: HTMLElement) {
    references.current = references.current.filter(Boolean).concat(ref);
  }
  function getHeights() {
    const heights = references.current.map((ref) =>
      ref?.getBoundingClientRect()
    );
    console.log(heights);
  }
  const clonedChildren = React.Children.map(children, (child) => {
    return React.cloneElement(child as any, {
      ref: getReference
    });
  });
  return <LayoutWrapper onMount={getHeights}>{clonedChildren}</LayoutWrapper>;
};

getCsvDataList <- function(startId = 1, endId = 10, maxConsecutiveNulls = 5) {

  dataList <- list()
  consecutiveNullCount <- 0

  for(id in startId:endId) {
    csvLink <- buildFibreIdLink(fibreId = id)
    newData <- tryCatch(expr = {
      read.csv(csvLink)
    }, error = function(e) {return(NULL)})
    if(is.null(newData)) {
      consecutiveNullCount <- consecutiveNullCount +1
    } else {
      dataList <- c(dataList,list(newData))
      consecutiveNullCount <- 0
    }
    if(consecutiveNullCount == maxConsecutiveNulls) {
      print(paste0("reached maxConsecutiveNulls at id ",id))
      break;
    }
  }

  return(dataList)
}

