import 'package:provider/provider.dart';
class ContactDetailsStreamWrapper extends StatefulWidget {
  const ContactDetailsStreamWrapper(this.contactId,
      {required this.builder, Key? key})
      : super(key: key);
  final String contactId;
  final Widget Function(Contact contact) builder;
  @override
  State<ContactDetailsStreamWrapper> createState() =>
      _ContactDetailsStreamWrapperState();
}
class _ContactDetailsStreamWrapperState
    extends State<ContactDetailsStreamWrapper> {
  Stream<Contact?>? contactStream;
  @override
  void initState() {
    contactStream = ContactDataService.getContactById(
        context.read<PropertyManagementProvider>().currentPropertyManagementId!,
        widget.contactId);
    super.initState();
  }
  @override
  Widget build(BuildContext context) {
    return StreamBuilder<Contact?>(
        stream: contactStream,
        builder: (context, contactSnapshot) {
          if (contactSnapshot.connectionState == ConnectionState.waiting) {
            return LoadingWidget();
          }
          if (contactSnapshot.data == null) {
            return Center(
                child: SizedBox(
              width: 600,
              child: ErrorCard(
                  message: 'Das Unternehmen konnte nicht geladen werden \n' +
                      contactSnapshot.error.toString()),
            ));
          }
          return widget.builder(contactSnapshot.data!);
        });
  }
}

import styled from 'styled-components'
export const Ball=
()=>
{
  let offsetX:number;
  let offsetY:number;
  const move=(e:MouseEvent)=>
  {
    const el=e.target as HTMLDivElement
    el.style.left = `${e.pageX-offsetX}px`
    el.style.top = `${e.pageY-offsetY}px`
  }
  const add=(e:React.MouseEvent<HTMLElement>):void =>
  {
    e.preventDefault()
    const el=e.target as HTMLDivElement
    offsetX=e.clientX - el.getBoundingClientRect().left
    offsetY=e.clientY- el.getBoundingClientRect().top
    el.addEventListener('mousemove',move)
  }
  const remove=(e:React.MouseEvent<HTMLElement>):void=>{
    const el=e.target as HTMLDivElement
    el.removeEventListener('mousemove',move)
  }
  const Wrapper=styled.div`
  width: 50px;
  height: 50px;
  border-radius: 29px;
  box-shadow: 0 0 6px;
  position: absolute;
  top: 0px;
  left: 0px;
  background-color: rgb(0,0,0,0.5);
  cursor:pointer;
  z-indez:99;
  `
  return (
    <Wrapper onMouseDown={ add }  onMouseUp={remove}/>
  )
}

// Get the time stamp (in milliseconds) that the timer started
var timerStart = window.localStorage.getItem('timerStart')
if (!timerStart){
	// If a timer was never started, start one
	timerStart = new Date().getTime()
	window.localStorage.setItem('timerStart', timerStart)
}
// Update the timer every 1/10th of a second
setInterval(timer, 100);
function timer() {
	// Use the start time to computer the number of
	// seconds left on the timer
    var count = 7 * 60 - Math.round((new Date().getTime() - timerStart) / 1000)
    if (count < 0) {
        clearInterval(timer);
        return;
    }
    var seconds = count % 60;
    var minutes = Math.floor(count / 60);
    minutes %= 60;
    document.getElementById("timer").innerHTML = (minutes).toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false}) + ":" + (seconds).toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false});
}

