        val request = Request.Builder()
            .url(url)
            .build()
        GlobalScope.launch {
            okHttpClient.newCall(request).execute().body()?.string()?.let { html ->
                GlobalScope.launch(Dispatchers.Main) {
                    val newHtml = replace(html)
