    &lt;span class=&quot;halfStyle&quot; data-content=&quot;A&quot;&gt;A&lt;/span&gt;

    &lt;hr/&gt;
    &lt;p&gt;Automated:&lt;/p&gt;

    &lt;span class=&quot;textToHalfStyle&quot;&gt;Half-style, please.&lt;/span&gt;

&lt;!-- end snippet --&gt;

