        attachEvent(&#39;onDOMContentLoaded&#39;, handler); // Internet Explorer 9+ :(
        attachEvent(&#39;onload&#39;, handler);
        attachEvent(&#39;onscroll&#39;, handler);
        attachEvent(&#39;onresize&#39;, handler);
    }
