    input.addEventListener(&#39;input&#39;, resizeInput); // bind the &quot;resizeInput&quot; callback on &quot;input&quot; event
    resizeInput.call(input); // immediately call the function

    function resizeInput() {
      this.style.width = this.value.length + &quot;ch&quot;;
    }



