         name: ko.observable(&quot;Bob&quot;)
      };
    
      var viewModelB = {
         name: ko.observable(&quot;Ted&quot;)
      };
    
      ko.applyBindings(viewModelA, document.getElementById(&quot;one&quot;));
      ko.applyBindings(viewModelB, document.getElementById(&quot;two&quot;));
