    var val = &quot;Fish&quot;;
    var sel = document.getElementById(&#39;sel&#39;);
    document.getElementById(&#39;btn&#39;).onclick = function() {
      var opts = sel.options;
      for (var opt, j = 0; opt = opts[j]; j++) {
