        display: flex;
        align-items: center; /* vertical */
        justify-content: center; /* horizontal */
    }

&lt;!-- language: lang-html --&gt;

    &lt;div id=&quot;parent&quot;&gt;
        &lt;div&gt;hello world&lt;/div&gt;
